// ==UserScript==
// @name         txt阅读器（仿pixiv，多主题）
// @namespace    http://tampermonkey.net/
// @version      2.0.0
// @description  访问txt时，美化阅读样式，有多个主题可供切换
// @author       Yesaye
// @match        *://*/*.txt
// @match        file:///*/*.txt
// @grant        none
// @grant        GM_setValue
// @grant        GM_getValue
// @license      MIT
// @icon         data:image/jpeg;base64,
// @downloadURL https://update.greasyfork.org/scripts/536343/txt%E9%98%85%E8%AF%BB%E5%99%A8%EF%BC%88pixiv%E9%A3%8E%E6%A0%BC%EF%BC%89.user.js
// @updateURL https://update.greasyfork.org/scripts/536343/txt%E9%98%85%E8%AF%BB%E5%99%A8%EF%BC%88pixiv%E9%A3%8E%E6%A0%BC%EF%BC%89.meta.js
// ==/UserScript==

(function () {
    'use strict';

    // 主题定义
    const themes = {
        light: {
            name: '明亮',
            background: '#f5f5f5',
            containerBg: '#ffffff',
            textColor: '#1f1f1f',
            btnBg: '#ffffff',
            btnColor: '#23272e',
            btnBorder: '#cccccc'
        },
        classic: {
            name: '经典',
            background: '#fafafa',
            containerBg: '#f0f0f0',
            textColor: '#222',
            btnBg: '#e0e0e0',
            btnColor: '#222',
            btnBorder: '#bdbdbd'
        },
        paper: {
            name: '纸张',
            background: '#fdf6e3',
            containerBg: '#ffffff',
            textColor: '#333333',
            btnBg: '#f5f2e7',
            btnColor: '#333333',
            btnBorder: '#e0e0e0'
        },
        sepia: {
            name: '羊皮纸',
            background: '#f4ecd8',
            containerBg: '#f9f6f2',
            textColor: '#5b4636',
            btnBg: '#e9e1c8',
            btnColor: '#5b4636',
            btnBorder: '#d6cfc2'
        },
        gray: {
            name: '灰色',
            background: '#e0e0e0',
            containerBg: '#f5f5f5',
            textColor: '#424242',
            btnBg: '#bdbdbd',
            btnColor: '#212121',
            btnBorder: '#9e9e9e'
        },
        bluegray: {
            name: '蓝灰',
            background: '#eceff1',
            containerBg: '#cfd8dc',
            textColor: '#263238',
            btnBg: '#b0bec5',
            btnColor: '#263238',
            btnBorder: '#78909c'
        },

        // 暗色风格
        dark: {
            name: '暗黑',
            background: '#23272e',
            containerBg: '#181a1b',
            textColor: '#e0e0e0',
            btnBg: '#23272e',
            btnColor: '#e0e0e0',
            btnBorder: '#444'
        },
        nightblue: {
            name: '夜蓝',
            background: '#1a2233',
            containerBg: '#222c3a',
            textColor: '#b3c7e6',
            btnBg: '#2a3550',
            btnColor: '#b3c7e6',
            btnBorder: '#3b4a6b'
        },
        nord: {
            name: '北极',
            background: '#2e3440',
            containerBg: '#3b4252',
            textColor: '#d8dee9',
            btnBg: '#4c566a',
            btnColor: '#eceff4',
            btnBorder: '#81a1c1'
        },
        monokai: {
            name: '莫诺凯',
            background: '#272822',
            containerBg: '#383830',
            textColor: '#f8f8f2',
            btnBg: '#49483e',
            btnColor: '#a6e22e',
            btnBorder: '#fd971f'
        },
        dracula: {
            name: '德古拉',
            background: '#282a36',
            containerBg: '#44475a',
            textColor: '#f8f8f2',
            btnBg: '#6272a4',
            btnColor: '#f1fa8c',
            btnBorder: '#bd93f9'
        },
        solarized: {
            name: '日光',
            background: '#fdf6e3',
            containerBg: '#eee8d5',
            textColor: '#657b83',
            btnBg: '#eee8d5',
            btnColor: '#586e75',
            btnBorder: '#93a1a1'
        },

        // 彩色/特殊风格
        pink: {
            name: '粉色',
            background: '#ffe4ec',
            containerBg: '#fff0f6',
            textColor: '#ad1457',
            btnBg: '#f8bbd0',
            btnColor: '#ad1457',
            btnBorder: '#f06292'
        },
        ocean: {
            name: '海洋',
            background: '#e0f7fa',
            containerBg: '#b2ebf2',
            textColor: '#006064',
            btnBg: '#4dd0e1',
            btnColor: '#006064',
            btnBorder: '#00bcd4'
        },
        coffee: {
            name: '咖啡',
            background: '#ece0d1',
            containerBg: '#d7ccc8',
            textColor: '#4e342e',
            btnBg: '#bcaaa4',
            btnColor: '#4e342e',
            btnBorder: '#8d6e63'
        },
        green: {
            name: '绿色',
            background: '#e8f5e9',
            containerBg: '#c8e6c9',
            textColor: '#1b5e20',
            btnBg: '#a5d6a7',
            btnColor: '#1b5e20',
            btnBorder: '#81c784'
        },
        forest: {
            name: '森林',
            background: '#e8f5e9',
            containerBg: '#a5d6a7',
            textColor: '#2e7d32',
            btnBg: '#66bb6a',
            btnColor: '#1b5e20',
            btnBorder: '#388e3c'
        },
        sunset: {
            name: '落日',
            background: '#fff3e0',
            containerBg: '#ffe0b2',
            textColor: '#e65100',
            btnBg: '#ffb74d',
            btnColor: '#e65100',
            btnBorder: '#ff9800'
        },
        lavender: {
            name: '薰衣草',
            background: '#f3e8ff',
            containerBg: '#e1bee7',
            textColor: '#6a1b9a',
            btnBg: '#ce93d8',
            btnColor: '#6a1b9a',
            btnBorder: '#ab47bc'
        }
    };

    // 读取主题（优先GM_getValue，其次localStorage）
    let currentTheme = 'light';
    let canUseLocalStorage = true;
    try {
        // 测试localStorage可用性
        localStorage.getItem('txt_reader_theme');
    } catch (e) {
        canUseLocalStorage = false;
    }
    if (typeof GM_getValue === 'function') {
        try {
            currentTheme = GM_getValue('txt_reader_theme', canUseLocalStorage ? localStorage.getItem('txt_reader_theme') || 'light' : 'light');
        } catch (e) {
            currentTheme = 'light';
        }
    } else if (canUseLocalStorage) {
        try {
            currentTheme = localStorage.getItem('txt_reader_theme') || 'light';
        } catch (e) {
            currentTheme = 'light';
        }
    } else {
        currentTheme = 'light';
    }

    // 创建主题下拉框
    const themeSelect = document.createElement('select');
    themeSelect.style.position = 'fixed';
    themeSelect.style.top = '20px';
    themeSelect.style.right = '20px';
    themeSelect.style.zIndex = 9999;
    themeSelect.style.borderRadius = '6px';
    themeSelect.style.padding = '8px 16px';
    themeSelect.style.boxShadow = '0 2px 8px 0 rgba(0,0,0,0.04)';
    themeSelect.style.cursor = 'pointer';
    themeSelect.style.transition = 'all 0.2s';

    // 填充下拉框选项
    const themeKeys = Object.keys(themes);
    themeKeys.forEach(key => {
        const option = document.createElement('option');
        option.value = key;
        option.textContent = themes[key].name || key;
        themeSelect.appendChild(option);
    });
    themeSelect.value = currentTheme;
    document.body.appendChild(themeSelect);

    // 创建容器
    var text_containter = document.createElement("div");
    text_containter.id = "text_containter";

    var text_box = document.createElement("div");
    text_box.id = "text_box";

    text_containter.appendChild(text_box);
    document.body.appendChild(text_containter);

    // 将原始文本放入 text_box
    var pre = document.getElementsByTagName("pre")[0];
    if (pre) {
        text_box.textContent = pre.textContent;
        pre.remove();
    }

    // 注入自定义样式
    var style = document.createElement('style');
    style.type = "text/css";
    document.head.appendChild(style);

    function applyTheme(themeName) {
        const theme = themes[themeName];
        style.textContent = `
        html { background-color: ${theme.background}; }
        body {
            margin: 0;
            padding: 0;
        }
        #text_containter {
            margin: 30px auto;
            padding: 60px 0;
            width: 912px;
            background-color: ${theme.containerBg};
        }
        #text_box {
            color: ${theme.textColor};
            max-width: 620px;
            margin: auto;
            font-size: 16px;
            line-height: 2;
            background-color: ${theme.containerBg};
            font-family: "Avenir Next", Avenir, "Source Sans", "Noto Sans", Roboto, Verdana, "Pingfang SC", "Hiragino Sans GB", "Lantinghei SC", "Source Han Sans CN", "Noto Sans CJK SC", "Microsoft Yahei", DengXian, YuGothic, "Hiragino Kaku Gothic Pro", Meiryo, "Source Han Sans", "Source Han Sans JP", "Noto Sans CJK JP", "Pingfang TC", "Pingfang HK", "Hiragino Sans CNS", "Lantinghei TC", "Source Han Sans TW", "Source Han Sans HK", "Noto Sans CJK TC", "Microsoft JhengHei", "Apple SD Gothic Neo", "Source Han Sans K", "Source Han Sans KR", "Noto Sans CJK KR", "Malgun Gothic", sans-serif;
            font-feature-settings: normal;
            overflow-wrap: break-word;
            white-space: pre-wrap;
            text-align: justify;
        }
        `;
        // 下拉框样式跟随主题
        themeSelect.style.background = theme.btnBg;
        themeSelect.style.color = theme.btnColor;
        themeSelect.style.border = `1px solid ${theme.btnBorder}`;
    }

    // 初始主题
    applyTheme(currentTheme);

    // 切换主题事件
    themeSelect.onchange = function () {
        currentTheme = themeSelect.value;
        if (canUseLocalStorage) {
            try {
                localStorage.setItem('txt_reader_theme', currentTheme);
            } catch (e) { }
        }
        if (typeof GM_setValue === 'function') {
            try {
                GM_setValue('txt_reader_theme', currentTheme);
            } catch (e) { }
        }
        applyTheme(currentTheme);
    };
})();